package org.usfirst.frc93.Team93Robot2015.commands;

import edu.wpi.first.wpilibj.command.Command;

import org.usfirst.frc93.Team93Robot2015.Robot;
import org.usfirst.frc93.Team93Robot2015.RobotMap;
import org.usfirst.frc93.Team93Robot2015.subsystems.Drive;

/**
 * This command drives the robot based off the reading of a line follower
 * 
 * @codereview ColbyMcKinley: This class does not read a line follower, it just
 *             sets the steering to 0, which means it will not turn.
 * 
 * @codereview ColbyMcKinley: This does not look like there is any input for
 *             reading a line. Is this command necessary after the introduction
 *             to 'line following code' in VisionProcessor.java.
 */
public class DriveForwardLineFollow extends Command {

    private double m_driveDistance;
    private double m_maxError;
    private int m_errorTimer;

    /**
     * 
     * @param driveDistance
     *            the amount to drive forwards
     * @param maxError
     *            the maximum error accepted before finishing the command
     */
    public DriveForwardLineFollow(double driveDistance, double maxError) {

        m_driveDistance = driveDistance;
        m_maxError = maxError;

        // Use requires() here to declare subsystem dependencies
        requires(Robot.drive);
    }

    // Called just before this Command runs the first time
    @Override
    /**
     * This function sets up the sensor for the robot to read.
     * @codereview ColbyMcKinley:  This function does not read a sensor, it drives to a certain point and will not turn.
     */
    protected void initialize() {
        Drive.resetSensors();
        RobotMap.driveSpeedControl.reset();
        RobotMap.driveSpeedControl.enable();
        RobotMap.driveSpeedControl.setSetpoint(m_driveDistance);
        RobotMap.driveLineFollowSteering.reset();
        RobotMap.driveLineFollowSteering.enable();
        RobotMap.driveLineFollowSteering.setSetpoint(0.0);
        RobotMap.steerAndSpeed.setSteeringGain(0.5);
    }

    // Called repeatedly when this Command is scheduled to run
    @Override
    protected void execute() {
        // PID Controller is moving the motors.
    }

    // Make this return true when this Command no longer needs to run execute()
    @Override
    /**
     * This function determines if we have reached our goal, and if we should continue driving or not
     */
    protected boolean isFinished() {
        // returns true when error is low enough for 0.5s
        double currentError = RobotMap.driveSpeedControl.getError();
        if (currentError <= m_maxError) {
            m_errorTimer += 20;
            if (m_errorTimer >= 500) {
                return true;
            }
        }
        else {
            m_errorTimer = 0;
        }
        return false;
    }

    // Called once after isFinished returns true
    @Override
    protected void end() {
        RobotMap.driveSpeedControl.disable();
        RobotMap.driveLineFollowSteering.disable();
        Drive.setAllMotors(0.0);
    }

    // Called when another command which requires one or more of the same
    // subsystems is scheduled to run
    @Override
    protected void interrupted() {
        this.end();
    }
}
